var berlinClock=function(t){this.dom=$(t),this.update()};berlinClock.prototype.update=function(t){t=t||new Date;this.current_seconds(t.getSeconds()),this.current_minutes(t.getMinutes()),this.current_hours(t.getHours())},berlinClock.prototype.current_minutes=function(t){var o=t%5,n=(t-o)/5;this.dom.find(".minute-top.row > div").each((function(t){$(this).toggleClass("on",--n>=0)})),this.dom.find(".minute-bottom.row > div").each((function(t){$(this).toggleClass("on",--o>=0)}))},berlinClock.prototype.current_hours=function(t){var o=t%5,n=(t-o)/5;this.dom.find(".hour-top.row > div").each((function(t){$(this).toggleClass("on",--n>=0)})),this.dom.find(".hour-bottom.row > div").each((function(t){$(this).toggleClass("on",--o>=0)}))},$(document).ready((function(){var t=new berlinClock("#berlinClock");t.update(),setInterval((function(){t.update()}),1e3)}));