setInterval(()=>{var r=new Date;var o=r.getHours();var e=r.getMinutes();var a=r.getSeconds();var t=Math.floor(o/5);var s=Math.floor(o%5);var l=Math.floor(e/5);var c=Math.floor(e%5);var d=document.querySelectorAll(".hx5 > div");var v=document.querySelectorAll(".hx1 > div");var f=document.querySelectorAll(".mx5 > div");var n=document.querySelectorAll(".mx1 > div");var i=a;++i;document.querySelector(".sec").style.transform="rotate("+6*i+"deg)";var m=0;for(m=0;m<t;m++)d[m].classList.add("on");if(0==t)Array.from(d).forEach(r=>r.classList.remove("on"));for(m=0;m<s;m++)v[m].classList.add("on");if(0==s)Array.from(v).forEach(r=>r.classList.remove("on"));for(m=0;m<l;m++)f[m].classList.add("on");if(0==l)Array.from(f).forEach(r=>r.classList.remove("on"));for(m=0;m<c;m++)n[m].classList.add("on");if(0==c)Array.from(n).forEach(r=>r.classList.remove("on"));if(o<=7||o>=20)document.querySelector("body").style.backgroundBlendMode="hard-light"},1e3);